map = DATA.readlines.map(&:chomp)

x = map[0].index('|')
y = 0

dx = 0
dy = 1

steps = 0
loop do
  while map[y][x] != '+'
    if y < 0 or y >= map.length or x < 0 or x >= map[y].length or map[y][x] == ' '
      p steps
      exit
    end

    x += dx
    y += dy
    steps += 1
  end

  if [dx, dy] != [0, -1] && y < map.length-1 && map[y + 1][x] =~ /^[|A-Za-z]$/
    dx, dy = 0, 1
  elsif [dx, dy] != [0, 1] && y > 0 && map[y - 1][x] =~ /^[|A-Za-z]$/
    dx, dy = 0, -1
  elsif [dx, dy] != [-1, 0] && x < map[y].length-1 && map[y][x + 1] =~ /^[-A-Za-z]$/
    dx, dy = 1, 0
  elsif [dx, dy] != [1, 0] && x > 0 && map[y][x - 1] =~ /^[-A-Za-z]$/
    dx, dy = -1, 0
  else
    break
  end

  x += dx
  y += dy
  steps += 1
end

__END__
